            HashMap<String,Integer> mp=new HashMap<>();
            for(int i=0;i<keywords.length;i++)
            {
                if(!mp.containsKey(keywords[i]))
                {
                    mp.put(keywords[i], 1);
                }
            }
            String text_arr[]=text_frm_image.split(" ");

            for(int i=0;i<text_arr.length;i++)
            {
                text_arr[i]=text_arr[i].toLowerCase();
            }

            ArrayList<Integer> list=new ArrayList<>();
            for(int k=1;k<5;k++)
            {
                for(int i=0;i<text_arr.length && i+k-1<text_arr.length;i++)
                {
                    String temp="";
                    for(int j=i;j<i+k && j<text_arr.length;j++)
                    {
                        temp=temp+ text_arr[j] + " ";
                    }
                    System.out.println(temp);
                    temp=temp.substring(0, temp.length()-1);
                    if(mp.containsKey(temp))
                    {
                        list.add(i);
                        list.add(i+k-1);
                    }

                }
            }

            for(int i=0;i<list.size() && i+1<list.size();i=i+2)
            {
                System.out.println(list.get(i)+" "+list.get(i+1));
                if(text_arr[list.get(i)].charAt(0)!='<')
                    text_arr[list.get(i)]="<h4><i><b>"+text_arr[list.get(i)];
                if(text_arr[list.get(i+1)].charAt(text_arr[list.get(i+1)].length()-1)!='>')
                    text_arr[list.get(i+1)]=text_arr[list.get(i+1)]+"</b></i></h4>";
            }

            String ans="";
            for(int i=0;i<text_arr.length;i++)
            {
                ans+=" "+text_arr[i];
            }
            textView.setText(Html.fromHtml(ans));

